userDefinedLinkHintCss:
div > .vimiumHintMarker {
  padding: 2px 3px;
  background-color: #eceff4;
  border: 3px;
  border-radius: 0px;
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 18px;
  font-weight: bold;
  text-shadow: none;
  color: #2f343f;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #bf616a;
}


#vomnibar {
  box-shadow: none;
  border: none;
  border-radius: 0;
  background-color: #2e3440;
  width: calc(70%);
  top: 0%;
  left: 15%;
}

#vomnibar .vomnibarSearchArea {
  box-shadow: none;
  border: none;
  background-color: #2e3440;

}

#vomnibar input {
 border: none;
 background-color: #2e3440;
 color: #f7f7f7;
 box-shadow: none;
}
#vomnibar input::selection {
    background: #475164;
}

#vomnibar ul {
 background-color: #2e3440;
 color: #f7f7f7;
 margin-top: 0;
 margin-bottom: 0;  
}


#vomnibar li {
 background-color: #2e3440;
 color: #f7f7f7;
 border: none;
}

.vimiumReset, div.vimiumReset, span.vimiumReset, table.vimiumReset, a.vimiumReset, a:visited.vimiumReset, a:link.vimiumReset, a:hover.vimiumReset, td.vimiumReset, tr.vimiumReset {
   color: #f7f7f7;
}
#vomnibar li .vomnibarSource {
   color: #c49ec8;
   font-weight: bolder;
}
#vomnibar li em, #vomnibar li .vomnibarTitle {
   color: #f7f7f7;
}
#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #f7f7f7;
}
#vomnibar li .vomnibarMatch {
  color: #b1ffb6;
  font-weight: bold;
}
#vomnibar li .vomnibarUrl {
   color: #73b5f0;
   font-weight: bold;
}
#vomnibar li.vomnibarSelected {
    background-color: #475164;
}



div.vimiumHUD {
    box-shadow: none;
    border: none;
    background-color: #2e3440;
    border-radius: 0px; 
    width: 100%;
}
div.vimiumHUD .hud-find {
    background: #2e3440;
    border: none;
}
div.vimiumHUD .vimiumHUDSearchArea {
    box-shadow: none;
    border: none;
    background-color: #2e3440;
}
div.vimiumHUD .vimiumHUDSearchAreaInner {
    color: #73b5f0;
    font-weight: bold;
}
div.vimiumHUD span#hud-find-input, div.vimiumHUD span#hud-match-count {
    color: #fff;
    display: inline;
    outline: none;
    white-space: nowrap;
    overflow-y: hidden;
}
